from collections import defaultdict


class Solution(object):
    def getHint(self, secret, guess):
        """
        :type secret: str
        :type guess: str
        :rtype: str
        """

        bulls = 0
        cows = 0

        secret = [c for c in secret]
        guess = [c for c in guess]
        n = len(secret)

        d = defaultdict(int)

        for i in range(n):
            if secret[i] == guess[i]:
                bulls += 1
                secret[i] = '#'
                guess[i] = '#'
            else:
                d[secret[i]] += 1

        for i in range(n):
            if guess[i] != '#':
                if d[guess[i]] > 0:
                    cows += 1
                    d[guess[i]] -= 1

        return '{}A{}B'.format(bulls, cows)


s = Solution()
print(s.getHint('1122', '1222'))

print(s.getHint('1123', '0111'))
print(s.getHint('1807', '7810'))
print(s.getHint(
    "",
    ""))
